from aiogram import Router, F
from aiogram.filters import Command
from aiogram.types import Message, CallbackQuery


import app.keyboards as kb
from app.database import set_user


router = Router()


@router.message(Command('start'))
async def start(message: Message):
    await set_user(message.from_user.id, message.from_user.username,  message.from_user.first_name)
    await message.answer_photo(photo="https://i.ibb.co/fGKVpfj2/16-C1-CD23-C.png", caption="<b>–ü—Ä–∏–≤–µ—Ç! üëã –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ –±–æ—Ç–∞ GerandNow! üé¨</b>\n\n<i>–° —ç—Ç–æ–≥–æ –º–æ–º–µ–Ω—Ç–∞ —Ç—ã –≤—Å–µ–≥–¥–∞ –±—É–¥–µ—à—å –≤ –∫—É—Ä—Å–µ –≤—Å–µ—Ö –Ω–æ–≤—ã—Ö –≤–∏–¥–µ–æ, —à–æ—Ä—Ç—Å –∏ –ø—Ä—è–º—ã—Ö —ç—Ñ–∏—Ä–æ–≤. üìπüî•\n–ë–æ–ª—å—à–µ –Ω–µ –Ω—É–∂–Ω–æ –ø—Ä–æ–≤–µ—Ä—è—Ç—å –∫–∞–Ω–∞–ª –≤—Ä—É—á–Ω—É—é ‚Äî –±–æ—Ç –ø—Ä–∏—à–ª—ë—Ç —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ —Å—Ä–∞–∑—É, –∫–∞–∫ —Ç–æ–ª—å–∫–æ –≤—ã–π–¥–µ—Ç –Ω–æ–≤–æ–µ –≤–∏–¥–µ–æ! üöÄ</i>\n\n<b>–ß—Ç–æ —Ç–µ–±—è –∂–¥—ë—Ç:</b>\n‚Ä¢ –°–≤–µ–∂–∏–µ –≤–∏–¥–µ–æ –∏ —à–æ—Ä—Ç—Å —Å –∫–∞–Ω–∞–ª–∞ üéû\n‚Ä¢ –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è –æ –ø—Ä—è–º—ã—Ö —ç—Ñ–∏—Ä–∞—Ö –∏ –ø—Ä–µ–º—å–µ—Ä–∞—Ö üì¢\n‚Ä¢ –ö—Ä–∞—Ç–∫–∏–µ –æ–ø–∏—Å–∞–Ω–∏—è –∏ —Å—Å—ã–ª–∫–∏ –Ω–∞ –≤–∏–¥–µ–æ –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ üîó\n‚Ä¢ –í–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å –≤—Å–µ–≥–¥–∞ –±—ã—Ç—å –≤ —Ü–µ–Ω—Ç—Ä–µ —Å–æ–±—ã—Ç–∏–π  –≤—Å–µ–ª–µ–Ω–Ω–æ–π –ì–µ—Ä–∞–Ω–¥ ‚ú®", reply_markup=kb.start_menu, parse_mode="HTML")


@router.message(Command('about'))
async def about(message: Message):
    await message.answer_photo(photo="https://i.ibb.co/MD4mQNNX/16-B4544-D0.png", caption="<b>–û –Ω–∞—Å üé¨</b>\n\n–ü—Ä–∏–≤–µ—Ç! üëã –ú—ã ‚Äì –∫–æ–º–∞–Ω–¥–∞, –∫–æ—Ç–æ—Ä–∞—è —Å–æ–∑–¥–∞–ª–∞ —ç—Ç–æ–≥–æ –±–æ—Ç–∞ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ –¥–ª—è —Ñ–∞–Ω–∞—Ç–æ–≤ –∫–∞–Ω–∞–ª–∞ –ì–µ—Ä–∞–Ω–¥. üìπüî•\n\n–ù–∞—à–∞ —Ü–µ–ª—å ‚Äì —Å–¥–µ–ª–∞—Ç—å —Ç–∞–∫, —á—Ç–æ–±—ã —Ç—ã –Ω–∏–∫–æ–≥–¥–∞ –Ω–µ –ø—Ä–æ–ø—É—Å–∫–∞–ª –Ω–æ–≤—ã–µ –≤–∏–¥–µ–æ, —à–æ—Ä—Ç—Å –∏ –ø—Ä—è–º—ã–µ —ç—Ñ–∏—Ä—ã. \n–° —ç—Ç–∏–º –±–æ—Ç–æ–º —Ç–µ–±–µ –±–æ–ª—å—à–µ –Ω–µ –Ω—É–∂–Ω–æ –ø—Ä–æ–≤–µ—Ä—è—Ç—å –∫–∞–Ω–∞–ª –≤—Ä—É—á–Ω—É—é ‚Äì –º—ã –ø—Ä–∏—à–ª—ë–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ —Å—Ä–∞–∑—É, –∫–∞–∫ —Ç–æ–ª—å–∫–æ –≤—ã–π–¥–µ—Ç —á—Ç–æ-—Ç–æ –Ω–æ–≤–æ–µ! üöÄ\n\n<b>–ß—Ç–æ –º—ã –ø—Ä–µ–¥–ª–∞–≥–∞–µ–º:</b>\n‚Ä¢ –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è –æ —Å–≤–µ–∂–∏—Ö –≤–∏–¥–µ–æ –∏ —à–æ—Ä—Ç—Å üéû\n‚Ä¢ –°—Å—ã–ª–∫–∏ –Ω–∞ –≤–∏–¥–µ–æ –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ üîó\n‚Ä¢ –û–ø–æ–≤–µ—â–µ–Ω–∏—è –æ –ø—Ä—è–º—ã—Ö —ç—Ñ–∏—Ä–∞—Ö –∏ –ø—Ä–µ–º—å–µ—Ä–∞—Ö üì¢\n‚Ä¢ –õ—ë–≥–∫–∏–π –∏ —É–¥–æ–±–Ω—ã–π —Å–ø–æ—Å–æ–± –±—ã—Ç—å –≤ –∫—É—Ä—Å–µ –≤—Å–µ–≥–æ –ø—Ä–æ–∏—Å—Ö–æ–¥—è—â–µ–≥–æ –Ω–∞ –∫–∞–Ω–∞–ª–µ –ì–µ—Ä–∞–Ω–¥ ‚ú®\n\n–ù–µ –∑–∞–±—É–¥—å <b>–≤—Å—Ç—É–ø–∏—Ç—å –≤ –Ω–∞—à Telegram-–∫–∞–Ω–∞–ª</b>, —á—Ç–æ–±—ã –±—ã—Ç—å –≤ –∫—É—Ä—Å–µ –≤—Å–µ—Ö –Ω–æ–≤–æ—Å—Ç–µ–π, —É—á–∞—Å—Ç–≤–æ–≤–∞—Ç—å –≤ –æ–±—Å—É–∂–¥–µ–Ω–∏—è—Ö –∏ –Ω–µ –ø—Ä–æ–ø—É—Å–∫–∞—Ç—å —ç–∫—Å–∫–ª—é–∑–∏–≤–Ω—ã–π –∫–æ–Ω—Ç–µ–Ω—Ç! üí¨", reply_markup=kb.about_menu, parse_mode="HTML")


@router.callback_query(F.data == 'about')
async def about2(callback: CallbackQuery):
    await callback.message.answer_photo(photo="https://i.ibb.co/MD4mQNNX/16-B4544-D0.png", caption="<b>–û –Ω–∞—Å üé¨</b>\n\n–ü—Ä–∏–≤–µ—Ç! üëã –ú—ã ‚Äì –∫–æ–º–∞–Ω–¥–∞, –∫–æ—Ç–æ—Ä–∞—è —Å–æ–∑–¥–∞–ª–∞ —ç—Ç–æ–≥–æ –±–æ—Ç–∞ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ –¥–ª—è —Ñ–∞–Ω–∞—Ç–æ–≤ –∫–∞–Ω–∞–ª–∞ –ì–µ—Ä–∞–Ω–¥. üìπüî•\n\n–ù–∞—à–∞ —Ü–µ–ª—å ‚Äì —Å–¥–µ–ª–∞—Ç—å —Ç–∞–∫, —á—Ç–æ–±—ã —Ç—ã –Ω–∏–∫–æ–≥–¥–∞ –Ω–µ –ø—Ä–æ–ø—É—Å–∫–∞–ª –Ω–æ–≤—ã–µ –≤–∏–¥–µ–æ, —à–æ—Ä—Ç—Å –∏ –ø—Ä—è–º—ã–µ —ç—Ñ–∏—Ä—ã. \n–° —ç—Ç–∏–º –±–æ—Ç–æ–º —Ç–µ–±–µ –±–æ–ª—å—à–µ –Ω–µ –Ω—É–∂–Ω–æ –ø—Ä–æ–≤–µ—Ä—è—Ç—å –∫–∞–Ω–∞–ª –≤—Ä—É—á–Ω—É—é ‚Äì –º—ã –ø—Ä–∏—à–ª—ë–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ —Å—Ä–∞–∑—É, –∫–∞–∫ —Ç–æ–ª—å–∫–æ –≤—ã–π–¥–µ—Ç —á—Ç–æ-—Ç–æ –Ω–æ–≤–æ–µ! üöÄ\n\n<b>–ß—Ç–æ –º—ã –ø—Ä–µ–¥–ª–∞–≥–∞–µ–º:</b>\n‚Ä¢ –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è –æ —Å–≤–µ–∂–∏—Ö –≤–∏–¥–µ–æ –∏ —à–æ—Ä—Ç—Å üéû\n‚Ä¢ –°—Å—ã–ª–∫–∏ –Ω–∞ –≤–∏–¥–µ–æ –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ üîó\n‚Ä¢ –û–ø–æ–≤–µ—â–µ–Ω–∏—è –æ –ø—Ä—è–º—ã—Ö —ç—Ñ–∏—Ä–∞—Ö –∏ –ø—Ä–µ–º—å–µ—Ä–∞—Ö üì¢\n‚Ä¢ –õ—ë–≥–∫–∏–π –∏ —É–¥–æ–±–Ω—ã–π —Å–ø–æ—Å–æ–± –±—ã—Ç—å –≤ –∫—É—Ä—Å–µ –≤—Å–µ–≥–æ –ø—Ä–æ–∏—Å—Ö–æ–¥—è—â–µ–≥–æ –Ω–∞ –∫–∞–Ω–∞–ª–µ –ì–µ—Ä–∞–Ω–¥ ‚ú®\n\n–ù–µ –∑–∞–±—É–¥—å <b>–≤—Å—Ç—É–ø–∏—Ç—å –≤ –Ω–∞—à Telegram-–∫–∞–Ω–∞–ª</b>, —á—Ç–æ–±—ã –±—ã—Ç—å –≤ –∫—É—Ä—Å–µ –≤—Å–µ—Ö –Ω–æ–≤–æ—Å—Ç–µ–π, —É—á–∞—Å—Ç–≤–æ–≤–∞—Ç—å –≤ –æ–±—Å—É–∂–¥–µ–Ω–∏—è—Ö –∏ –Ω–µ –ø—Ä–æ–ø—É—Å–∫–∞—Ç—å —ç–∫—Å–∫–ª—é–∑–∏–≤–Ω—ã–π –∫–æ–Ω—Ç–µ–Ω—Ç! üí¨", reply_markup=kb.about_menu, parse_mode="HTML")
